            type: ""POST"",
            url: '/Admin/Reply',
            data: {
                ""Id"": id,
                ""text"": text,
                ""subjectId"": subjectId
            },
            success: function (response) {
                document.getElementById(""btnReply-"" + id).style.backgroundColor = ""red"";
                document.getElementById(""btnReply-"" + id).value = ""ارسال شد"";
